// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

contract Election{
    // struct candidate
    // candidate count
    // mapping candidate
    // constructor
    struct Candidate{
        uint id;
        string name;
        uint votecount;
    }

    // candidate count 
    uint public candidatesCount;

    mapping(uint => Candidate) public candidates;

    mapping(address => bool) public votedornot;
    // address a = 

    event electionupdated(
        uint indexed _candidateId
        );
    constructor(){
        // the code that we want to initiate
        addCandidate("Donald Trumph");
        addCandidate("Barack Obama");
    }

    // add candidates
    function addCandidate(string memory name) private{
        candidatesCount++;
        candidates[candidatesCount] = Candidate(candidatesCount, name, 0);
    }


    function Vote(uint _id) public{
        // the person has not voted again
        require(!votedornot[msg.sender], 'You have already voted for the Election');
        // the id that the person has input is available
        require(candidates[_id].id != 0, 'The id doesnt exist');
        // increase the vote count and change boolean value true

        candidates[_id].votecount +=1;

        // record the voter has voted
        votedornot[msg.sender] = true;

        //trigger voted event
        emit electionupdated(candidates[_id].id);
    }
}
